const SEARCH_ENGINE_ID = '36cfb770c239c4595';

async function performSearch() {
  searching = true;
  error = false;
  searchPerformed = false;

  try {
    const response = await fetch(`https://www.googleapis.com/customsearch/v1?key=${API_KEY}&cx=${SEARCH_ENGINE_ID}&q=${encodeURIComponent(searchQuery)}`);

    if (response.ok) {
      const data = await response.json();
      searchResults = data.items || [];
      searchPerformed = true;
    } else {
      throw new Error('Search failed');
    }
  } catch (e) {
    console.error('Search error:', e);
    error = true;
  } finally {
    searching = false;
  }
}

